class VaultExportService:
    def __init__(self, vault_path: str):
        self.vault = Path(vault_path)
    def run(self):
        exported = 0
        for note in Note.objects.filter(conflict=False):
            if note.content_hash == note.last_synced_hash:
                continue
            path = self.vault / note.external_id
            path.parent.mkdir(parents=True, exist_ok=True)

class VaultSyncService:
    def __init__(self, vault_path):
        self.vault_path = vault_path
    def run(self):
        import_result = VaultImportService(self.vault_path).run()
        export_result = VaultExportService(self.vault_path).run()
        return {
            "import": import_result,
            "export": export_result
        }

